/* eslint-disable react/jsx-no-target-blank */
import React from 'react'
import ""

function Cards(props) {
	return (
		<div class="row">
			<div class="col-4">
				<a href={props.link} target="_blank">
					<img src={props.imgsrc}
						alt="Product1"
					/>
					<h4>{props.title}</h4>
					<p>&#x20B9;{props.price}</p>
				</a>
			</div>
		</div>
	)
}

export default Cards
